if selected=='Dashboard':
    st.subheader('Welcome to Dashboard')
    uploaded_file = st.file_uploader("Upload a CSV file", type=["csv"])
    if uploaded_file is not None:
        st.subheader("Uploaded CSV Data")
        try:
            df = pd.read_csv(uploaded_file)
            st.write(df.head())
        except Exception as e:
            st.error(f"Error reading the CSV file: {str(e)}")


    #Buttons

    selected_button = st.radio("Select a button:", ("Descriptive", "Diagnostic", "Prescriptive", "Predictive"))



    if selected_button == "Descriptive":
        st.write("You selected Descriptive.")
        selected_feature = st.radio("Select a button:", ("Measure of frequency", "Central Tendency", "Dispersion and Variance"))
        st.sidebar.title("Select Attributes")
        target_attribute = st.sidebar.selectbox("Select the target attribute:", df.columns)
        other_attributes = st.sidebar.multiselect("Select other attributes:", df.columns)
        selected_data = df[other_attributes+[target_attribute]]
        st.write("Selected Data:")
        st.write(selected_data.head())
        if selected_feature=='Central Tendency':
            for selected_attribute in other_attributes:
                st.header(f"Central Tendency Analysis for {selected_attribute}")
                central_tendency_stats = calculate_central_tendency_stats(df, target_attribute, selected_attribute)
                st.write("Central Tendency Statistics:")
                st.write(central_tendency_stats)
                plot_central_tendency_graph(df, target_attribute, selected_attribute)


        elif selected_feature == 'Dispersion and Variance':
            st.write("You selected Dispersion and Variance.")
            selected_attribute_dispersion = st.selectbox("Select an attribute for Dispersion and Variance:", other_attributes)
            st.header(f"Dispersion and Variance Analysis for {selected_attribute_dispersion}")
            if selected_attribute_dispersion not in selected_data.columns:
                st.error("The specified column does not exist in the selected data.")
            else:
                calculate_variance_and_dispersion(selected_data, selected_attribute_dispersion)

        elif selected_feature == 'Measure of frequency':
            st.write("You selected Measure of Frequency.")
            selected_attribute_frequency = st.selectbox("Select an attribute for Measure of Frequency:", other_attributes)
            st.header(f"Frequency Measures for {selected_attribute_frequency}")
            
            # Check if the selected column is present in the selected_data DataFrame
            if selected_attribute_frequency not in selected_data.columns:
                st.error("The specified column does not exist in the selected data.")
            else:
                calculate_frequency_measures(selected_data, selected_attribute_frequency)





    elif selected_button == "Diagnostic":
        st.sidebar.title("Select Attributes")
        target_attribute = st.sidebar.selectbox("Select the target attribute:", df.columns)
        other_attributes = st.sidebar.multiselect("Select other attributes:", df.columns)
        selected_data = df[other_attributes+[target_attribute]]
        st.write("Selected Data:")
        st.write(selected_data.head())
        st.write("You selected Diagnostic.")
        diagnostic(selected_data,target_attribute,other_attributes)

    elif selected_button == "Prescriptive":
        st.write("You selected Prescriptive.")
        prescriptive_analysis()

    elif selected_button == "Predictive":
        st.sidebar.title("Select Attributes")
        target_attribute = st.sidebar.selectbox("Select the target attribute:", df.columns)
        other_attributes = st.sidebar.multiselect("Select other attributes:", df.columns)
        selected_data = df[other_attributes+[target_attribute]]
        st.write("Selected Data:")
        st.write(selected_data.head())
        st.write("You selected Predictive.")
        perform_linear_regression_on_dataset(selected_data)
        ANN(selected_data,target_attribute,other_attributes)
        irispred()



        
